void IniciaTabla() {
        DefaultTableModel modelo = new DefaultTableModel();
        modelo.addColumn("Nombre");
        modelo.addColumn("Edad");
        Tabla.setModel(modelo);
    }
    private void BotonAgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonAgregarActionPerformed
        Alumnos.agregarPersona(Texto1.getText(), Texto2.getText());
    }//GEN-LAST:event_BotonAgregarActionPerformed

    private void BotonBorrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonBorrarActionPerformed

        Alumnos.eliminarPersona(javax.swing.JOptionPane.showInputDialog("Dame el nombre a eliminar"));
    }//GEN-LAST:event_BotonBorrarActionPerformed

    private void BotonConsultarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonConsultarActionPerformed
        DefaultTableModel modelo = new DefaultTableModel();
        modelo.addColumn("Nombre");
        modelo.addColumn("Edad");
        Tabla.setModel(modelo);
        String[] mios = new String[2];
        ResultSet dos;
        try {
            dos = Alumnos.Consulta();

            while (dos.next()) {
                mios[0] = dos.getString(1);
                mios[1] = dos.getString(2);                
                modelo.addRow(mios);
            }
            Tabla.setModel(modelo);

        } catch (SQLException ex) {
            Logger.getLogger(CBD.class.getName()).log(Level.SEVERE, null, ex);
        }


    }//GEN-LAST:event_BotonConsultarActionPerformed

